configfiles.configfile:
begin mrbayes;
    set Seed=$seed;
    set Swapseed=$swapseed;
    set quitonerror=yes;
    execute $data;
    outgroup $outgroup;
    $model;
    lset rates=$rates;
#if str($filename) != ""
    mcmcp filename=$filename;
#end if
    mcmcp ngen=$ngen
      nrun=$nrun
      nchain=$nchain
      checkfreq=$checkfreq
      samplefreq=$samplefreq
      printfreq=$printfreq
      stoprule=$stoprule
      burninfrac=$burninfrac;
#if int($ngen) >= int($checkfreq)
    $append;
#end if
    mcmc;
    $sump
    $sumt
#if str($plot) == "plot"
    plot;
#end if
end;

citations.citation:
@ARTICLE {huelsenbeckjpronquistf.2001,
            author = {Huelsenbeck JP, Ronquist F.},
            title = {MRBAYES: Bayesian inference of phylogenetic trees.},
            journal = {Bioinformatics},
            year = {2001},
            volume = {17},
            pages = {754-755},
            url = {http://www.ncbi.nlm.nih.gov/pubmed/11524383}
            }
